var ClusterAmaryllis = (function(){

  var hex = '0123456789ABCDEF'.split(''),

  hashtag = '#',

  socialIcon = {
    "Facebook": [ '<i class="icon-facebook-sign"></i>', 'icons-facebook-wrapper' ],
    "Twitter": [ '<i class="icon-twitter-sign"></i>', 'icons-twitter-wrapper' ],
    "Google+": [ '<i class="icon-google-plus-sign"></i>', 'icons-google-plus-wrapper' ],
    "Github": [ '<i class="icon-github-sign"></i>', 'icons-github-wrapper' ],
    "Pinterest": [ '<i class="icon-pinterest-sign"></i>', 'icons-pinterest-wrapper' ],
    "Tumblr": [ '<i class="icon-tumblr-sign"></i>', 'icons-tumblr-wrapper' ],
    "def": [ '', '' ]
  };


  return {
    getRandomColor: function() {
      var color = hashtag;

      for (var i=0; i<6; i++) {
        color += hex[Math.round(Math.random() * 15)];
      }
      return color;
    },
    getSocialIconByName: function( name ) {
      // remove newline, tab, space
      name = name.replace(/[\r\n\t\s]/g, "");
      return socialIcon[ name ][0] || socialIcon['def'][0];
    },
    getSocialIconId: function( name ) {
      name = name.replace(/[\r\n\t\s]/g, "");
      return socialIcon[ name ][1] || socialIcon['def'][1];
    }
  }
})();

jQuery(function($) {
  // backstretch
  $('#header').backstretch([
    //'//3.bp.blogspot.com/-CWbYSfCHJXY/UlAdKzPXRII/AAAAAAAAACU/Mn-LSE312LU/s1600/background1.jpg'
    'http://topwalls.net/wp-content/uploads/2012/07/ocean-blue-wave-.jpg'
    // 'http://pcdn.500px.net/14904099/4242ce5ad7453af37693e68bc50d4b708f06ef6a/5.jpg'
  ], {centeredY: false});

  // tabbed sidebar
  $('#sidebar2').find('.widget').each(function( idx, elem ){

    var text = $( elem ).find('h2').text(),
      renderText = ClusterAmaryllis.getSocialIconByName( text ) + ' <span class="hidden-lg">' + text + '</span>', 
      id = ClusterAmaryllis.getSocialIconId( text ),
      anchor = $('<a/>').attr({
        href: '#' + $( elem ).attr('id'),
        dataToggle: 'pill',
      }).addClass( id ).append( renderText ),
      li = $('<li/>').append( anchor );

    // hide widget title
    $( elem ).find('h2').hide();

    // add 'active' on first widget
    if ( idx == 0 ) {
      li.addClass('active');
      $( elem ).addClass('active');
    }

    $('#tab-sidebar').append( li );
    $( elem ).addClass('tab-pane');
  });

  // add tab event listener
  $('#tab-sidebar a').on('click', function ( e ) {
    e.preventDefault();
    $( this ).tab('show');
  });

  // bootstrap affix plugin
  $('#naver').affix({
    offset: {
      top: function() {
        var $win = $( window );

        if ( $win.width() >= 1200 ) {
          return 135;
        } else {
          return 195;
        }
      }
    }
  });

  // bootstrap selectpicker
  $('.selectpicker').selectpicker();
});
